#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\summer-gonner\traffica-front-vue3\node_modules\.pnpm\msw@2.4.9_typescript@5.6.3\node_modules\msw\cli\node_modules;D:\summer-gonner\traffica-front-vue3\node_modules\.pnpm\msw@2.4.9_typescript@5.6.3\node_modules\msw\node_modules;D:\summer-gonner\traffica-front-vue3\node_modules\.pnpm\msw@2.4.9_typescript@5.6.3\node_modules;D:\summer-gonner\traffica-front-vue3\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/summer-gonner/traffica-front-vue3/node_modules/.pnpm/msw@2.4.9_typescript@5.6.3/node_modules/msw/cli/node_modules:/mnt/d/summer-gonner/traffica-front-vue3/node_modules/.pnpm/msw@2.4.9_typescript@5.6.3/node_modules/msw/node_modules:/mnt/d/summer-gonner/traffica-front-vue3/node_modules/.pnpm/msw@2.4.9_typescript@5.6.3/node_modules:/mnt/d/summer-gonner/traffica-front-vue3/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../msw/cli/index.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../msw/cli/index.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../msw/cli/index.js" $args
  } else {
    & "node$exe"  "$basedir/../msw/cli/index.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
